import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { remove, clear, updateQuantity } from "../Store/ProductSlice";
import jsPDF from "jspdf";
import { Link } from "react-router-dom";
import "./product.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";

const Cart = () => {
  const [hovered, setHovered] = useState(null);
  const dispatch = useDispatch();
  const items = useSelector((state) => state.product.products);
  const [totalPrice, setTotalPrice] = useState(0);

  const handleHover = (item) => {
    setHovered(item);
  };

  if (!items || items.length === 0) {
    return (
      <h1 className="font-bold text-5xl text-slate-400 flex item-center justify-center ">
        No products left
      </h1>
    );
  }

  const deleteItem = (item) => {
    dispatch(remove(item.title));
  };

  const removeAll = () => {
    dispatch(clear());
  };

  const genernatePDF = () => {
    const doc = new jsPDF();
    doc.text(10, 10, "Receipt");
    items.forEach((item, index) => {
      doc.text(10, 20 + index * 10, `${item.title}`);
    });
    doc.save("receipt.pdf");
  };

  const handleChange = (event, item) => {
    const newQuantity = parseInt(event.target.value, 10);
    if (!isNaN(newQuantity) && newQuantity >= 0) {
      // Update the quantity of the item
      dispatch(updateQuantity({ title: item.title, quantity: newQuantity }));
    }
  };

  const calculateTotalPrice = () => {
    const total = items.reduce((acc, item) => {
      return acc + item.newPrice * item.cartQuantity;
    }, 0);
    setTotalPrice(total);
  };

  return (
    <div className="container m-auto">
      <div className="relative overflow-x-auto shadow-md sm:rounded-lg mt-5">
        <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
          <thead className="text-xs text-gray-700 uppercase border shadow-slate-950 dark:text-gray-400">
            <tr className="py-4 my-3">
              <th scope="col" className="px-6 py-5">
                <span className="sr-only">Image</span>
              </th>
              <th scope="col" className="px-6 py-6">
                Product
              </th>
              <th scope="col" className="px-6 py-6">
                Price
              </th>
              <th scope="col" className="px-6 py-6">
                Quantity
              </th>
              <th scope="col" className="px-6 py-6">
                Subtotal
              </th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index} className="bg-white border-b">
                <td
                  className="w-32 p-4 relative"
                  onMouseEnter={() => handleHover(item)}
                >
                  <img
                    src={item.image}
                    alt="Product Image"
                    height={50}
                    width={50}
                  />
                  {hovered && (
                    <button
                      className="cross-icon"
                      onClick={() => deleteItem(item)}
                    >
                      <FontAwesomeIcon icon={faTimes} />
                    </button>
                  )}
                </td>
                <td className="px-6 py-4 font-semibold text-gray-900">
                  {item.title}
                </td>
                <td className="px-6 py-4 font-semibold text-gray-900">
                  ${item.newPrice}
                </td>
                <td className="px-6 py-4">
                  <div className="flex items-center space-x-3">
                    <div>
                      <input
                        type="number"
                        id={item.title}
                        className="w-14 border border-gray-300 text-sm block px-2.5 py-1"
                        placeholder="1"
                        value={item.cartQuantity}
                        required
                        onChange={(e) => handleChange(e, item)}
                      />
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 text-gray-900">
                  ${item.newPrice * item.cartQuantity}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="flex justify-between mt-3">
        <Link to="/product">
          <button className="border px-3 py-3">Return to products</button>
        </Link>
        <button onClick={removeAll} className="border px-4">
          Remove All
        </button>
      </div>
      <div className="border mt-5 h-64 w-80 flex items-center justify-center">
        <div>
          <div>
            <div>
              <div className="flex">
                <h1>Sub Total</h1>
                <h1 className="padding">${totalPrice}</h1>
              </div>
              <div className="flex">
                <p>Shipping</p>
                <p className="padding">Free</p>
              </div>
              <div className="flex">
                <p>Cart Total:</p>
                <p className="padding">${totalPrice}</p>
              </div>
            </div>
          </div>
          <div className="flex justify-center items-center mt-5 text-white">
            <button className="border px-3 py-3 bg-red-600" onClick={genernatePDF}>
              Download Receipt
            </button>
          </div>
        </div>
      </div>
      <div></div>
    </div>
  );
};

export default Cart;
