import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { remove, clear } from "../Store/ProductSlice";
import jsPDF from "jspdf";
import { Link } from "react-router-dom";
import "./product.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faX } from "@fortawesome/free-solid-svg-icons";

const Cart = () => {
  const [hovered, setHovered] = useState(null);
  const [totalPrice, settotalprice] = useState([]);
  const dispatch = useDispatch();
  const items = useSelector((state) => state.product.products);

  const handleHover = (item) => {
    setHovered(item);
  };
  const leaveMouse = () => {
    setHovered(null);
  };

  if (!items || items.length === 0) {
    return (
      <h1 className="font-bold text-5xl text-slate-400 flex item-center justify-center ">
        no products left
      </h1>
    );
  }

  const deleteItem = (item) => {
    dispatch(remove(item.title));
  };

  const removeAll = () => {
    dispatch(clear());
  };
  const genernatePDF = () => {
    const doc = new jsPDF();

    doc.text(10, 10, "Receipt");
    doc.text(10, 10, "helo");

    doc.save("receipt.pdf");
  };

  const handleChnage = (event,item) => {
    const handleValue = event.target.value;
    items.map((value, index) => {
      console.log(value.newPrice);
      console.log(index);
      const total = handleValue * value.newPrice;
      settotalprice([...items,id]);
    });
  };
  console.log(totalPrice);

  return (
    <div className="container m-auto">
      <div className="relative overflow-x-auto shadow-md sm:rounded-lg mt-5">
        <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
          <thead className="text-xs text-gray-700 uppercase border shadow-slate-950 dark:text-gray-400">
            <tr className="py-4 my-3">
              <th scope="col" className="px-6 py-5">
                <span className="sr-only">Image</span>
              </th>
              <th scope="col" className="px-6 py-6">
                Product
              </th>
              <th scope="col" className="px-6 py-6 ">
                Price
              </th>
              <th scope="col" className="px-6 py-6">
                Quantity
              </th>
              <th scope="col" className="px-6 py-6">
                Subtotal
              </th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index} className="bg-white border-b">
                <td
                  className="w-32 p-4 relative"
                  onMouseEnter={() => handleHover(item)}
                  onMouseLeave={leaveMouse}
                >
                  <img
                    src={item.image}
                    alt="Product Image"
                    height={50}
                    width={50}
                  />
                  {hovered && (
                    <button
                      className="cross-icon"
                      onClick={() => deleteItem(item)}
                    >
                      <FontAwesomeIcon icon={faX} />
                    </button>
                  )}
                </td>
                <td className="px-6 py-4 font-semibold text-gray-900">
                  {item.title}
                </td>
                <td className="px-6 py-4 font-semibold text-gray-900">
                  ${item.newPrice}
                </td>
                <td className="px-6 py-4">
                  <div className="flex items-center space-x-3">
                    <div>
                      <input
                        type="number"
                        id="first_product"
                        className="w-14 border border-gray-300 text-sm block px-2.5 py-1"
                        placeholder="1"
                        value={item.cartQuantity}
                        required
                        onChange={(e) => handleChnage(e, item)}
                      />
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 text-gray-900">{totalPrice}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="flex justify-between mt-3">
        <Link to="/product">
          <button className="border px-3 py-3">Return to product</button>
        </Link>
        <button onClick={removeAll} className="border px-4">
          Remove All
        </button>
      </div>
      <div className="border mt-5 h-64 w-80 flex items-center justify-center ">
        <div>
          <div>
            <div>
              <div className="flex">
                <h1>Sub Total</h1>
                <h1 className="padding">560</h1>
              </div>
              <div className="flex">
                <p>Shipping</p>
                <p className="padding"> free</p>
              </div>
              <div className="flex">
                <p>Cart Total</p>
                <p className="padding">:</p>
              </div>
            </div>
          </div>
          <div className="flex justify-center items-center mt-5 text-white">
            <button
              className="border px-3 py-3  bg-red-600"
              onClick={genernatePDF}
            >
              Download Receipt
            </button>
          </div>
        </div>
      </div>
      <div></div>
    </div>
  );
};

export default Cart;
