import { createSlice } from "@reduxjs/toolkit";

const favouriteSlice = createSlice({
  name: "favouriteItem",
  initialState: {
    favouriteProduct: [],
  },
  reducers: {
    Handlefavourite(state, action) {
      state.favouriteProduct = [...state.favouriteProduct, action.payload];
    },
    removeAllIte(state,action){
      state.favouriteProduct=[]
    }
  },
});
export const { Handlefavourite,remove } = favouriteSlice.actions;
export default favouriteSlice;
