import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { removeAllItem,deleteItem } from "../Store/FavouriteSlice";

import "./product.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faX } from "@fortawesome/free-solid-svg-icons";
const Likeditem = () => {
  const [hovered, setHovered] = useState();
  const dispatch = useDispatch();
  const items = useSelector((state) => state.favouriteItem.favouriteProduct);
  console.log(items);
  if (!items || items.length === 0) {
    return (
      <h1 className="font-bold text-5xl text-slate-400 flex item-center justify-center ">
        no products left
      </h1>
    );
  }
  const handleHover = (item) => {
    setHovered(item);
  };
  const leaveMouse = () => {
    setHovered(null);
  };
  const deleteAItem = (item) => {
    dispatch(deleteItem(item.title));
  };
  const removeAll = () => {
    dispatch(removeAllItem());
  };
  return (
    <div className="container m-auto">
      <div className="relative overflow-x-auto shadow-md sm:rounded-lg mt-5">
        <table className="w-full text-sm text-left ">
          <thead className="text-xs  uppercase border mb-8 ">
            <tr className="py-4 my-3">
              <th scope="col" className="px-6 py-5 mb-3">
                <span className="sr-only">Image</span>
              </th>
              <th scope="col" className="px-6 py-6">
                Product
              </th>

              <th scope="col" className="px-6 py-6">
                Price
              </th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index} className="bg-white border-b">
                <td
                  className="w-32 p-4 relative flex font-size"
                  onMouseEnter={() => handleHover(item)}
                  onMouseLeave={leaveMouse}
                >
                  <img
                    src={item.image}
                    alt="Product Image"
                    height={50}
                    width={50}
                  />
                  {hovered === item && (
                    <button
                      className="cross-icon"
                      onClick={() => deleteItem(item)}
                    >
                      <FontAwesomeIcon
                        icon={faX}
                        className="rounded-full border bg-red-600 px-1 py-1 text-white"
                      />
                    </button>
                  )}
                </td>
                <td className="px-6 py-4 font-semibold text-gray-900">
                  {item.title}
                </td>
                <td className="px-6 py-4 font-semibold text-gray-900">
                  ${item.newPrice}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="flex justify-end">
        <button className="border mt-8 px-8 py-4" onClick={removeAll}>
          Remove All
        </button>
      </div>
    </div>
  );
};

export default Likeditem;
